mapper.select:
	 select 
  from gas_card gc
   left outer join sys_driver driver on gc.card_no = driver.card_id
  where 1=1


      and card_no = #{card_no,jdbcType=VARCHAR}


      and card_type = #{card_type,jdbcType=VARCHAR}


      and card_status = #{card_status,jdbcType=VARCHAR}


      and operator LIKE CONCAT(CONCAT('%',  #{operator,jdbcType=VARCHAR}), '%')


       and workstation = #{workstation,jdbcType=VARCHAR}


       workstation_resp = #{workstation_resp,jdbcType=VARCHAR}


       batch_no = #{batch_no,jdbcType=VARCHAR}


      and storage_time between CONCAT(#{storage_time_after,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{storage_time_before,jdbcType=VARCHAR},' 23:59:59')

select 
from gas_card gc
left outer join sys_driver driver on gc.card_no = driver.card_id
where 1=1


    and (card_no = #{card_no,jdbcType=VARCHAR} or (driver.mobile_phone = #{card_no,jdbcType=VARCHAR}))


    and card_type = #{card_type,jdbcType=VARCHAR}


    and operator = #{operator,jdbcType=VARCHAR}


    and workstation = #{workstation,jdbcType=VARCHAR}


    workstation_resp = #{workstation_resp,jdbcType=VARCHAR}


    batch_no = #{batch_no,jdbcType=VARCHAR}


    and storage_time between CONCAT(#{storage_time_after,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{storage_time_before,jdbcType=VARCHAR},' 23:59:59')


   and (card_status = 3 or card_status = 4)

select 
from gas_card gc
where 1=1


    and CAST(card_no as unsigned) > #{startID,jdbcType=INTEGER}


    and CAST(card_no as unsigned) < #{endID,jdbcType=INTEGER}


    and workstation = #{sys_gas_station_id, jdbcType=VARCHAR}


  and card_status=2

select 
from gas_card gc
left outer join sys_driver driver on gc.card_no = driver.card_id
where 1=1


    and (card_no = #{card_no,jdbcType=VARCHAR} or (driver.mobile_phone = #{card_no,jdbcType=VARCHAR})


    and card_type = #{card_type,jdbcType=VARCHAR}


    and card_status = #{card_status,jdbcType=VARCHAR}


    and operator = #{operator,jdbcType=VARCHAR}


    and workstation = #{workstation,jdbcType=VARCHAR}


    workstation_resp = #{workstation_resp,jdbcType=VARCHAR}


    batch_no = #{batch_no,jdbcType=VARCHAR}


    and storage_time between CONCAT(#{storage_time_after,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{storage_time_before,jdbcType=VARCHAR},' 23:59:59')

select 
from gas_card gc
 left outer join sys_driver driver on gc.card_no = driver.card_id
 where 1=1


    and card_no = #{card_no,jdbcType=VARCHAR}


    and card_type = #{card_type,jdbcType=VARCHAR}


    and operator = #{operator,jdbcType=VARCHAR}


    and workstation = #{workstation,jdbcType=VARCHAR}


    workstation_resp = #{workstation_resp,jdbcType=VARCHAR}


    batch_no = #{batch_no,jdbcType=VARCHAR}


    and storage_time between CONCAT(#{storage_time_after,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{storage_time_before,jdbcType=VARCHAR},' 23:59:59')


   and CAST(card_no as unsigned) > #{startID,jdbcType=INTEGER} and CAST(card_no as unsigned)  < #{endID,jdbcType=INTEGER}
   order by CAST(card_no as unsigned) and (card_status = 3 or card_status = 4)
