mapper.update:
update sysongy_charge_card
set password = #{password,jdbcType=VARCHAR},
  ownid = #{ownid,jdbcType=VARCHAR},
  use_time = #{useTime,jdbcType=TIMESTAMP},
  cash = #{cash,jdbcType=DECIMAL}
where id = #{id,jdbcType=VARCHAR}

update sysongy_charge_card
set password = #{password,jdbcType=VARCHAR},
  ownid = #{ownid,jdbcType=VARCHAR},
  use_time = #{useTime,jdbcType=TIMESTAMP},
  cash = #{cash,jdbcType=DECIMAL}
where ownid = #{ownid,jdbcType=VARCHAR}

mapper.select:
select

from sysongy_charge_card
where 1 = 1


    and id = #{id}


    and ownid = #{ownid}


    and  password = #{password}


    and  user_time = #{useTime}


    and  cash = #{cash}
