mapper.select:
	select 

  from boss_doc bd 
  where bd.is_deleted = 0 and bd.document_name = #{documentName}

mapper.insert:
insert into boss_doc (boss_doc_id, parent_id, document_name, 
  document_type, remarks, file_path, 
  is_deleted, created_date, modified_date
  )
values (#{bossDocId,jdbcType=VARCHAR}, #{parentId,jdbcType=VARCHAR}, #{documentName,jdbcType=VARCHAR}, 
  #{documentType,jdbcType=INTEGER}, #{remarks,jdbcType=VARCHAR}, #{filePath,jdbcType=VARCHAR}, 
  #{isDeleted,jdbcType=INTEGER}, #{createdDate,jdbcType=TIMESTAMP}, #{modifiedDate,jdbcType=TIMESTAMP}
  )
