mapper.select:
select 

from physique_health where category_name like #{categoryName}
order by listorder asc

mapper.insert:
insert into physique_health (category_id, pcategory_id, category_name, 
  sm, wh, bz, listorder, 
  author_id, author_name, modifier_id, 
  modifier_name, create_date, modify_date
  )
values (#{categoryId,jdbcType=VARCHAR}, #{pcategoryId,jdbcType=VARCHAR}, #{categoryName,jdbcType=VARCHAR}, 
  #{sm,jdbcType=VARCHAR}, #{wh,jdbcType=VARCHAR}, #{bz,jdbcType=VARCHAR}, #{listorder,jdbcType=INTEGER}, 
  #{authorId,jdbcType=VARCHAR}, #{authorName,jdbcType=VARCHAR}, #{modifierId,jdbcType=VARCHAR}, 
  #{modifierName,jdbcType=VARCHAR}, #{createDate,jdbcType=TIMESTAMP}, #{modifyDate,jdbcType=TIMESTAMP}
  )
