mapper.select:
select

from mb_app_version
where
1=1

  and app_version_id = #{appVersionId,jdbcType=VARCHAR}


  and url = #{url,jdbcType=VARCHAR}


  and version = #{version,jdbcType=VARCHAR}


  and code = #{code,jdbcType=VARCHAR}


  and is_publish = #{isPublish,jdbcType=INTEGER}


  and remark = #{remark,jdbcType=VARCHAR}


  and DATE_FORMAT(created_date,"%Y/%m/%d")      >=   #{created_date_sel,jdbcType=VARCHAR} and   DATE_FORMAT(created_date,"%Y/%m/%d")     <=   #{updated_date_sel,jdbcType=VARCHAR}
