mapper.select:
select 

from mb_banner a, dist_city b
where a.is_deleted = 1 and a.city_id=b.city_id

  and a.title = #{title,jdbcType=VARCHAR}


  and a.img_path = #{imgPath,jdbcType=VARCHAR}


  and a.img_sm_path = #{imgSmPath,jdbcType=VARCHAR}


  and a.target_url = #{targetUrl,jdbcType=VARCHAR}


  and a.version = #{version,jdbcType=VARCHAR}


  and a.img_type = #{imgType,jdbcType=INTEGER}


  and a.sort = #{sort,jdbcType=INTEGER}


  and a.remark = #{remark,jdbcType=VARCHAR}


  and a.city_id = #{cityId,jdbcType=INTEGER}


  and a.created_date = #{createdDate,jdbcType=TIMESTAMP}


  and a.updated_date = #{updatedDate,jdbcType=TIMESTAMP}

order by a.sort asc

select * from mb_banner where is_deleted = 1 and img_type = #{0}
order by sort desc
limit 0,1
