mapper.select:
select

from sys_order
where 1=1

  and order_id = #{orderId,jdbcType=VARCHAR}


  and order_type = #{orderType,jdbcType=VARCHAR}

select s.order_id,so.deal_number,so.remark,s.order_type,s.operator,so.run_success,so.deal_date 
from sys_order s,sys_order_deal so 
where s.order_id = so.order_id and s.order_number= #{order_number,jdbcType=VARCHAR}

    and so.deal_number = #{deal_number,jdbcType=VARCHAR}


    and so.deal_date between CONCAT(#{deal_date_after,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{deal_date_before,jdbcType=VARCHAR},' 23:59:59')

select

from sys_order
  where 1=1 and is_discharge='0' and been_discharged='0'


    and order_number = #{orderNumber, jdbcType=VARCHAR}


    and operator_source_id = #{operatorSourceId, jdbcType=VARCHAR}

select 

from sys_order
where credit_account = #{userId,jdbcType=VARCHAR}
 and order_type in (#{CONSUME_BY_TRANSPORTION,jdbcType=VARCHAR},#{CONSUME_BY_DRIVER,jdbcType=VARCHAR})
 and order_date > #{order_date,jdbcType=TIMESTAMP}

select 
 sum(cash) as sumcash 
from sys_order
where debit_account = #{userId,jdbcType=VARCHAR}
 and order_type = #{CHARGE_TO_DRIVER,jdbcType=VARCHAR}

SELECT order_goods_id,a.order_id, price, number,sum_price,goods_type, b.order_id as orderID,order_number,order_type, order_date, cash, credit_account,debit_account, charge_type,channel,channel_number, operator,operator_source_id,operator_source_type, operator_target_type,is_discharge,discharge_order_id,consume_card,discharge_reason, been_discharged FROM sys_order_goods a left join sys_order b on a.order_id = b.order_id WHERE 1=1
and order_number = #{orderNumber, jdbcType=VARCHAR}
and operator_source_id = #{operatorSourceId, jdbcType=VARCHAR}
and order_date between CONCAT(#{storage_time_after,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{storage_time_before,jdbcType=VARCHAR},' 23:59:59')
and goods_type = #{goodsType, jdbcType=VARCHAR}

select sor.order_number,sor.order_type,sor.is_discharge,sor.cash,sd.full_name,sd.mobile_phone,sgs.gas_station_name, sog.goods_type,sog.price,sog.number,sog.sum_price,sor.order_date,sod.remark from sys_driver sd left join sys_order sor on sd.card_id = sor.consume_card left join sys_order_goods sog on sog.order_id = sor.order_id left join sys_order_deal sod on sor.order_id = sod.order_id left join sys_gas_station sgs on sgs.sys_gas_station_id = sor.operator_source_id where sor.order_id is not null
and sd.station_id = #{debitAccount,jdbcType=VARCHAR}
and sor.is_discharge = #{is_discharge,jdbcType=VARCHAR}
and (sd.full_name like CONCAT('%',CONCAT(#{orderNumber,jdbcType=VARCHAR},'%')) or sd.mobile_phone like CONCAT('%',CONCAT(#{orderNumber,jdbcType=VARCHAR},'%')))
and sor.order_date between CONCAT(#{startDate,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{endDate,jdbcType=VARCHAR},' 23:59:59')

select sor.order_number,sor.order_type,sor.is_discharge,tf.fleet_name,tv.plates_number,sgs.gas_station_name, sog.goods_type,sog.price,sog.number,sog.sum_price,sod.deal_date,sod.remark from tc_vehicle tv left join tc_fleet_vehicle tfv on tv.tc_vehicle_id = tfv.tc_vehicle_id left join tc_fleet tf on tfv.tc_fleet_id = tf.tc_fleet_id left join sys_order sor on sor.consume_card = tv.card_no left join sys_order_deal sod on sor.order_id = sod.order_id left join sys_order_goods sog on sog.order_id = sor.order_id left join sys_gas_station sgs on sgs.sys_gas_station_id = sor.operator_source_id where tv.is_deleted = '1' and tf.is_deleted = '1' and sor.order_id is not null
and tf.tc_fleet_id = #{orderId,jdbcType=VARCHAR}
and tv.station_id = #{debitAccount,jdbcType=VARCHAR}
and sor.is_discharge = #{is_discharge,jdbcType=VARCHAR}
and (tf.fleet_name like CONCAT('%',CONCAT(#{orderNumber,jdbcType=VARCHAR},'%')) or tv.plates_number like CONCAT('%',CONCAT(#{orderNumber,jdbcType=VARCHAR},'%')))
and sor.order_date between CONCAT(#{startDate,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{endDate,jdbcType=VARCHAR},' 23:59:59')
