mapper.insert:
insert into sys_user_account (sys_user_account_id, account_code, account_type, 
  account_balance, created_date, updated_date, account_status, deposit
  )
values (#{sysUserAccountId,jdbcType=VARCHAR}, #{accountCode,jdbcType=VARCHAR}, #{accountType,jdbcType=INTEGER}, 
  #{accountBalance,jdbcType=VARCHAR}, #{createdDate,jdbcType=TIMESTAMP}, #{updatedDate,jdbcType=TIMESTAMP}, 
  #{account_status,jdbcType=VARCHAR}, #{deposit,jdbcType=DECIMAL}
  )

mapper.update:
update sys_user_account set account_balance = (cast(account_balance as decimal(38, 9)) - cast(#{accountBalance,jdbcType=VARCHAR} as decimal(38, 9)))
  where  sys_user_account_id = #{sysUserAccountId,jdbcType=VARCHAR}
  and version = #{version,jdbcType=INTEGER}
