mapper.select:
select from sys_operation_log where 1=1
and log_platform = #{logPlatform,jdbcType=VARCHAR}
and created_date between CONCAT(#{created_date_after,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{created_date_before,jdbcType=VARCHAR},' 23:59:59')

mapper.insert:
insert into sys_operation_log (sys_operation_log_id, log_platform, system_module, 
  log_content, operator, role_name, 
  user_name, user_ip, created_date
  )
values (#{sysOperationLogId,jdbcType=VARCHAR}, #{logPlatform,jdbcType=VARCHAR}, #{systemModule,jdbcType=VARCHAR}, 
  #{logContent,jdbcType=VARCHAR}, #{operator,jdbcType=VARCHAR}, #{roleName,jdbcType=VARCHAR}, 
  #{userName,jdbcType=VARCHAR}, #{userIp,jdbcType=VARCHAR}, #{createdDate,jdbcType=TIMESTAMP}
  )
