mapper.select:
select 

  from sys_transportion
  where 1=1


      and sys_transportion_id = #{sys_transportion_id,jdbcType=VARCHAR}


      and transportion_name = #{transportion_name,jdbcType=VARCHAR}


      and status = #{status,jdbcType=VARCHAR}


      and expiry_date between CONCAT(#{expiry_date_after,jdbcType=VARCHAR},' 0:00:00') and CONCAT(#{expiry_date_before,jdbcType=VARCHAR},' 23:59:59')
